mapper.select:
select cbs.id,cbs.commodity_no,type,count,name from commodity_browse_statistics cbs,commodity c where cbs.commodity_no=c.commodity_no and type=#{type,jdbcType=INTEGER} order by count desc
limit 0,#{size,jdbcType=INTEGER}
limit 0,10

select cbs.id,cbs.commodity_no,type,count ,name from commodity_collection_statistics cbs,commodity c where cbs.commodity_no=c.commodity_no and type=#{type,jdbcType=INTEGER} order by count desc
limit 0,#{size,jdbcType=INTEGER}
limit 0,10

select cbs.id,cbs.commodity_no,type,count ,name from commodity_buy_statistics cbs,commodity c where cbs.commodity_no=c.commodity_no and type=#{type,jdbcType=INTEGER} order by count desc
limit 0,#{size,jdbcType=INTEGER}
limit 0,10

select cbs.id,cbs.store_no,cbs.commodity_no,type,count ,name,storename from store_commodity_buy_statistics cbs,store s, commodity c where cbs.store_no=s.store_no and cbs.commodity_no=c.commodity_no and type=#{type,jdbcType=INTEGER} and cbs.store_no=#{storeNo,jdbcType=VARCHAR} order by count desc
limit 0,#{size,jdbcType=INTEGER}
limit 0,10

select cbs.id,cbs.store_no,cbs.commodity_no,type,count ,name,storename from store_commodity_collection_statistics cbs,store s, commodity c where cbs.store_no=s.store_no and cbs.commodity_no=c.commodity_no and type=#{type,jdbcType=INTEGER} and cbs.store_no=#{storeNo,jdbcType=VARCHAR} order by count desc
limit 0,#{size,jdbcType=INTEGER}
limit 0,10
